import { Component, OnInit } from '@angular/core';
import { Problem } from "../../models/problem.model";

const PROBLEMS: Problem[] = [
    {
      id: 1,
      name: "haha",
      desc: `Testing with a and b`,
      difficulty: "easy"
    },
    {
      id: 2,
      name: "hehe",
      desc: `Testing with a, b and c`,
      difficulty: "medium"
    },
    {
      id: 3,
      name: "hoho",
      desc: `Testing with a, b, c and d`,
      difficulty: "hard"
    },
    {
        id: 4,
        name: "huhu",
        desc: `Testing with a, b, c, d and e`,
        difficulty: "super"
    }

]
@Component({
  selector: 'app-problem-list',
  template: `
    <div class="container">
      <div class="list-group">
        <a class="list-group-item" *ngFor="let problem of problems">
            <span class="{{'pull-left label difficulty diff-' + problem.difficulty.toLocaleLowerCase()}}">{{problem.difficulty}} </span>
            <strong class="title">{{problem.id}}.  {{problem.name}} </strong>
        </a>
      </div>
    </div>
  `,
  styles: [`
    .difficulty {
        min-width: 65px;
        margin-right: 10px;
    }

    .label.difficulty {
        padding-top: 0.6em;
        color: #fbfdfa;
        font-size: 12px;
    }

    .title {
        font-size: 1.2em;
    }

    .diff-easy {
        background-color: #42ebf4;
    }

    .diff-medium {
        background-color: #92cf5c;
    }

    .diff-hard {
        background-color: #dd0d1e;
    }

    .diff-super {
        background-color: #8d16e2;
    }
  `]
})
export class ProblemListComponent {
    problems: Problem[] | undefined;
    constructor() {}
    ngOnInit() {
        this.problems = PROBLEMS;
    }
}
